mapper.insert:

	SELECT NVL(MAX(bno),1000) + 1
	FROM board		   

INSERT INTO
	(
		bno
		, title
		, contents
		, writer
		, image
	)
VALUES
	(
		#{bno}
		, #{title}
		, #{contents}
		, #{writer}
		, #{image}
	)

mapper.update:
UPDATE board
	SET
		title = #{title}
		, contents = #{contents}
		, writer = #{writer}
		, updatedate = sysdate
		, image = #{image}
	WHERE bno = #{bno}
